import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  FlatList,
  StyleSheet,
} from 'react-native';

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [taskText, setTaskText] = useState('');
  const [editingTask, setEditingTask] = useState(null);

  const addTask = () => {
    if (taskText.trim() !== '') {
      if (editingTask !== null) {
        // Update existing task
        updateTask();
      } else {
        // Add new task
        setTasks((prevTasks) => [
          ...prevTasks,
          { id: Date.now().toString(), text: taskText },
        ]);
        setTaskText('');
      }
    }
  };

  const updateTask = () => {
    if (editingTask !== null && taskText.trim() !== '') {
      setTasks((prevTasks) =>
        prevTasks.map((task) =>
          task.id === editingTask.id ? { ...task, text: taskText } : task
        )
      );
      setTaskText('');
      setEditingTask(null);
    }
  };

  const deleteTask = (taskId) => {
    setTasks((prevTasks) => prevTasks.filter((task) => task.id !== taskId));
    setEditingTask(null);
  };

  const renderItem = ({ item }) => (
    <View style={styles.taskItem}>
      <Text>{item.text}</Text>
      <View style={styles.buttonsContainer}>
        <TouchableOpacity
          style={styles.editButton}
          onPress={() => {
            setEditingTask(item);
            setTaskText(item.text);
          }}
        >
          <Text style={styles.buttonText}>Edit</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.deleteButton}
          onPress={() => deleteTask(item.id)}
        >
          <Text style={styles.buttonText}>Delete</Text>
        </TouchableOpacity>
      </View>
    </View>
  );

  return (
    <View style={styles.container}>
      <TextInput
        style={styles.input}
        placeholder="Add or Edit a task"
        value={taskText}
        onChangeText={setTaskText}
      />
      <View style={styles.buttonRow}>
        <TouchableOpacity style={styles.addButton} onPress={addTask}>
          <Text style={styles.buttonText}>
            {editingTask ? 'Update Task' : 'Add Task'}
          </Text>
        </TouchableOpacity>
        {editingTask && (
          <TouchableOpacity
            style={styles.updateButton}
            onPress={() => {
              setTaskText('');
              setEditingTask(null);
            }}
          >
            <Text style={styles.buttonText}>Cancel</Text>
          </TouchableOpacity>
        )}
      </View>
      <FlatList data={tasks} keyExtractor={(item) => item.id} renderItem={renderItem} />
    </View>
  );
};

const styles = StyleSheet.create({
  // Your existing styles remain unchanged
});

export default TodoList;
