import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  FlatList,
  StyleSheet,
} from 'react-native';




const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 20,
      backgroundColor: '#f4f4f4',
    },
    input: {
      borderWidth: 1,
      borderColor: '#ccc',
      padding: 12,
      marginBottom: 10,
      borderRadius: 8,
      backgroundColor: '#fff',
    },
    addButton: {
      backgroundColor: '#3498db',
      padding: 12,
      alignItems: 'center',
      borderRadius: 8,
      marginBottom: 10,
    },
    updateButton: {
      backgroundColor: '#2ecc71', // Change the color to a green shade
      padding: 12,
      alignItems: 'center',
      borderRadius: 8,
      marginBottom: 10,
    },
    deleteButton: {
      backgroundColor: '#e74c3c',
      padding: 12,
      alignItems: 'center',
      borderRadius: 8,
      marginBottom: 10,
    },
    buttonText: {
      color: '#fff',
      fontWeight: 'bold',
    },
    taskItem: {
      borderWidth: 1,
      borderColor: '#ccc',
      padding: 15,
      marginBottom: 10,
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      borderRadius: 8,
      backgroundColor: '#fff',
    },
    buttonsContainer: {
      flexDirection: 'row',
    },
    buttonRow: {
      flexDirection: 'row',
      justifyContent: 'space-between',
    },
  });
  
  export default TodoList;
